var AuthzUtil = require('oae-authz/lib/util');
var Validator = module.exports.Validator = require('oae-util/lib/validator').Validator;

/**
 * Checks whether or not the string in context is a valid publication id
 *
 * Usage:
 * ```
 * var validator = new Validator();
 * validator.check(publicationId, error).isPublicationId();
 * ```
 */
Validator.prototype.isPublicationId = function() {
    if (!this.str) {
        this.error(this.msg || 'An empty publication id was provided');
    } else if (this.str.split(':').length < 3) {
        this.error(this.msg || 'A publication id must be of the format: "p:<tenantAlias>:<instanceId>". "' + this.str + '" does not satisfy that.');
    } else if (this.str.split(':')[0] !== 'p') {
        this.error(this.msg || 'A publication id must start with a \'p\'. "' + this.str + '" does not satisfy that.');
    } else if (AuthzUtil.isUserId(this.str)) {
        this.error(this.msg || 'A user id is not a valid resource id: ' + this.str);
    }
};
